    public void drawData(OLEDDisplay display, int offsetX, int offsetY){
        for(int[] point : data) {
            display.setPixel(point[0] + offsetX, point[1] + offsetY - height, true);
        }
    }

    public void drawDataCentered(OLEDDisplay display, int offsetX, int offsetY){
        for(int[] point : data) {
            display.setPixel(point[0] + offsetX - width/2, point[1] + offsetY - height, true);
        }
    }
